      const idToken = response.credential;
      // send idtoken to backend
    }
  };

  useEffect(() => {
    gapi.load('auth2', function() {
      gapi.auth2.init({
        client_id: '822420183545-p950fdql2o6ls85egkd47gtf4a6tujcc.apps.googleusercontent.com'
      });
    });

    const signinButton = document.querySelector('.g_id_signin');
    signinButton.addEventListener('click', () => {
      gapi.auth2.getAuthInstance().signIn();
    });
  }, []);


  return (
    <>
    <div className="container-fluid">
        <div className="accountc">
        
        <div className="row">
            <div className="col-md-8 pd account">
            <img src="./aliaslogodark 1.png" alt="" />
            <h2>Create your Alias account</h2>
            <div className="gbutton">
                <button onClick={handleGoogleSignIn} ><img src="./google.png" alt="" /> <span>Sign up with Google</span></button> 
