            { content: resultContent, role: 'assistant' },
          ]);
          setCurrentMessage('');
        }, 1);
      } else {
        const result = await response.json();
        toast.error(result.error);
      }

      setIsLoading(false);
    } catch (error) {
      console.error(error);
      toast.error(error.message);
