              placeholder="Ask a question."
              id="interviewInput" 
              class="input-box"
              ref={textAreaRef}
              rows={rows}
              value={message}
              onInput={handleInput}
            />
            <button onClick={sendMessage}><img src="./Reply.svg" alt="" className='intervieinputimgs'/></button>
            
         </div>
         </div>
           
        </div>
    </section></>
  )
}

